exceptions = @(@{ combined_id = '1234_5678_000000000000'; action = 'BLOCK_WRITE_EXECUTE' })})}
PS>Edit-FalconDeviceControlPolicy -Id <id> -Settings $Settings

$DiffOperations = @(@{ op = 'add'; path = '/rules/0'; value = @{ temp_id = '1'; name = 'First rule in a group';
    description = 'Example'; platform_ids = @('0'); enabled = $false; action = 'ALLOW'; direction = 'IN';
    address_family = 'NONE'; protocol = '6'; fields = @(@{ name = 'network_location'; type = 'set';
    values = @( 'ANY' )}); local_address = @(@{ address = '*'; netmask = 0 }); remote_address = @(@{
    address = '*'; netmask = 0 })}})
PS>$Group = Get-FalconFirewallGroup -Ids <id>
PS>$Rules = Get-FalconFirewallRule -Ids $Group.rule_ids
PS>$RuleIds = @('1') + $Group.rule_ids
PS>$RuleVersions = @('null') + $Rules.version
PS>Edit-FalconFirewallGroup -Id $Group.id -DiffOperations $DiffOperations -RuleIds $RuleIds
    -RuleVersions $RuleVersions

$Group = Get-FalconIoaGroup -Ids <id>
PS>$Rule = $Group.rules | Where-Object { $_.name -eq 'BugRule' }
PS>$Rule.enabled = $true
PS>($Rule.field_values | Where-Object {
    $_.name -eq 'GrandparentImageFilename' }).values[0].value = '.+updatebug.exe'
PS>Edit-FalconIoaRule -RulegroupId $Group.id -RuleUpdates $Rule

